     1→import { useState, useEffect, useMemo } from 'react';
     2→import type { Ausgaben, Kategorien } from '@/types/app';
     3→import { APP_IDS } from '@/types/app';
     4→import { LivingAppsService, extractRecordId, createRecordUrl } from '@/services/livingAppsService';
     5→import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
     6→import { Button } from '@/components/ui/button';
     7→import { Badge } from '@/components/ui/badge';
     8→import { Input } from '@/components/ui/input';
     9→import { Label } from '@/components/ui/label';
    10→import { Textarea } from '@/components/ui/textarea';
    11→import { Skeleton } from '@/components/ui/skeleton';
    12→import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
    13→import {
    14→  Dialog,
    15→  DialogContent,
    16→  DialogHeader,
    17→  DialogTitle,
    18→  DialogTrigger,
    19→} from '@/components/ui/dialog';
    20→import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
    21→import { AreaChart, Area, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';
    22→import { format, parseISO, startOfMonth, endOfMonth, isWithinInterval, subMonths } from 'date-fns';
    23→import { de } from 'date-fns/locale';
    24→import { Plus, TrendingUp, TrendingDown, AlertCircle, Receipt } from 'lucide-react';
    25→
    26→// Currency formatter for German locale
    27→function formatCurrency(value: number | null | undefined): string {
    28→  if (value == null) return '€ 0,00';
    29→  return new Intl.NumberFormat('de-DE', {
    30→    style: 'currency',
    31→    currency: 'EUR',
    32→  }).format(value);
    33→}
    34→
    35→// Category bar colors (teal gradient)
    36→const CATEGORY_COLORS = [
    37→  'hsl(175 45% 35%)',
    38→  'hsl(175 40% 45%)',
    39→  'hsl(175 35% 55%)',
    40→  'hsl(175 30% 65%)',
    41→];
    42→
    43→export default function Dashboard() {
    44→  const [ausgaben, setAusgaben] = useState<Ausgaben[]>([]);
    45→  const [kategorien, setKategorien] = useState<Kategorien[]>([]);
    46→  const [loading, setLoading] = useState(true);
    47→  const [error, setError] = useState<Error | null>(null);
    48→  const [dialogOpen, setDialogOpen] = useState(false);
    49→  const [submitting, setSubmitting] = useState(false);
    50→
    51→  // Form state
    52→  const [formData, setFormData] = useState({
    53→    betrag: '',
    54→    beschreibung: '',
    55→    kategorie: '',
    56→    datum: format(new Date(), 'yyyy-MM-dd'),
    57→    notizen: '',
    58→  });
    59→
    60→  // Fetch data
    61→  async function fetchData() {
    62→    try {
    63→      setLoading(true);
    64→      setError(null);
    65→      const [ausgabenData, kategorienData] = await Promise.all([
    66→        LivingAppsService.getAusgaben(),
    67→        LivingAppsService.getKategorien(),
    68→      ]);
    69→      setAusgaben(ausgabenData);
    70→      setKategorien(kategorienData);
    71→    } catch (err) {
    72→      setError(err instanceof Error ? err : new Error('Ein Fehler ist aufgetreten'));
    73→    } finally {
    74→      setLoading(false);
    75→    }
    76→  }
    77→
    78→  useEffect(() => {
    79→    fetchData();
    80→  }, []);
    81→
    82→  // Create kategorie lookup map
    83→  const kategorieMap = useMemo(() => {
    84→    const map = new Map<string, Kategorien>();
    85→    kategorien.forEach((k) => map.set(k.record_id, k));
    86→    return map;
    87→  }, [kategorien]);
    88→
    89→  // Get kategorie name by record ID
    90→  function getKategorieName(kategorieUrl: string | undefined): string {
    91→    if (!kategorieUrl) return 'Ohne Kategorie';
    92→    const id = extractRecordId(kategorieUrl);
    93→    if (!id) return 'Ohne Kategorie';
    94→    const kategorie = kategorieMap.get(id);
    95→    return kategorie?.fields.kategoriename || 'Ohne Kategorie';
    96→  }
    97→
    98→  // Current month calculations
    99→  const now = new Date();
   100→  const currentMonthStart = startOfMonth(now);
   101→  const currentMonthEnd = endOfMonth(now);
   102→  const lastMonthStart = startOfMonth(subMonths(now, 1));
   103→  const lastMonthEnd = endOfMonth(subMonths(now, 1));
   104→
   105→  // Filter expenses for current month
   106→  const currentMonthExpenses = useMemo(() => {
   107→    return ausgaben.filter((a) => {
   108→      if (!a.fields.datum) return false;
   109→      const date = parseISO(a.fields.datum);
   110→      return isWithinInterval(date, { start: currentMonthStart, end: currentMonthEnd });
   111→    });
   112→  }, [ausgaben, currentMonthStart, currentMonthEnd]);
   113→
   114→  // Filter expenses for last month
   115→  const lastMonthExpenses = useMemo(() => {
   116→    return ausgaben.filter((a) => {
   117→      if (!a.fields.datum) return false;
   118→      const date = parseISO(a.fields.datum);
   119→      return isWithinInterval(date, { start: lastMonthStart, end: lastMonthEnd });
   120→    });
   121→  }, [ausgaben, lastMonthStart, lastMonthEnd]);
   122→
   123→  // Calculate totals
   124→  const currentMonthTotal = useMemo(() => {
   125→    return currentMonthExpenses.reduce((sum, a) => sum + (a.fields.betrag || 0), 0);
   126→  }, [currentMonthExpenses]);
   127→
   128→  const lastMonthTotal = useMemo(() => {
   129→    return lastMonthExpenses.reduce((sum, a) => sum + (a.fields.betrag || 0), 0);
   130→  }, [lastMonthExpenses]);
   131→
   132→  // Calculate percentage change
   133→  const percentChange = useMemo(() => {
   134→    if (lastMonthTotal === 0) return null;
   135→    return ((currentMonthTotal - lastMonthTotal) / lastMonthTotal) * 100;
   136→  }, [currentMonthTotal, lastMonthTotal]);
   137→
   138→  // Category breakdown
   139→  const categoryBreakdown = useMemo(() => {
   140→    const groups = new Map<string, { name: string; total: number }>();
   141→
   142→    currentMonthExpenses.forEach((a) => {
   143→      const kategorieUrl = a.fields.kategorie;
   144→      const kategorieId = kategorieUrl ? extractRecordId(kategorieUrl) : 'none';
   145→      const name = getKategorieName(kategorieUrl);
   146→      const key = kategorieId || 'none';
   147→
   148→      if (!groups.has(key)) {
   149→        groups.set(key, { name, total: 0 });
   150→      }
   151→      const group = groups.get(key)!;
   152→      group.total += a.fields.betrag || 0;
   153→    });
   154→
   155→    return Array.from(groups.values())
   156→      .sort((a, b) => b.total - a.total)
   157→      .slice(0, 4);
   158→  }, [currentMonthExpenses, kategorieMap]);
   159→
   160→  // Daily spending chart data
   161→  const chartData = useMemo(() => {
   162→    const dailyTotals = new Map<number, number>();
   163→
   164→    currentMonthExpenses.forEach((a) => {
   165→      if (!a.fields.datum) return;
   166→      const day = parseISO(a.fields.datum).getDate();
   167→      dailyTotals.set(day, (dailyTotals.get(day) || 0) + (a.fields.betrag || 0));
   168→    });
   169→
   170→    const daysInMonth = currentMonthEnd.getDate();
   171→    const data = [];
   172→    for (let i = 1; i <= daysInMonth; i++) {
   173→      data.push({
   174→        day: i,
   175→        betrag: dailyTotals.get(i) || 0,
   176→      });
   177→    }
   178→    return data;
   179→  }, [currentMonthExpenses, currentMonthEnd]);
   180→
   181→  // Recent expenses (sorted by date desc)
   182→  const recentExpenses = useMemo(() => {
   183→    return [...ausgaben]
   184→      .sort((a, b) => {
   185→        const dateA = a.fields.datum || '';
   186→        const dateB = b.fields.datum || '';
   187→        return dateB.localeCompare(dateA);
   188→      })
   189→      .slice(0, 8);
   190→  }, [ausgaben]);
   191→
   192→  // Handle form submission
   193→  async function handleSubmit(e: React.FormEvent) {
   194→    e.preventDefault();
   195→    if (!formData.betrag || !formData.beschreibung) return;
   196→
   197→    setSubmitting(true);
   198→    try {
   199→      await LivingAppsService.createAusgabenEntry({
   200→        betrag: parseFloat(formData.betrag.replace(',', '.')),
   201→        beschreibung: formData.beschreibung,
   202→        datum: formData.datum,
   203→        kategorie: formData.kategorie
   204→          ? createRecordUrl(APP_IDS.KATEGORIEN, formData.kategorie)
   205→          : undefined,
   206→        notizen: formData.notizen || undefined,
   207→      });
   208→
   209→      // Reset form and close dialog
   210→      setFormData({
   211→        betrag: '',
   212→        beschreibung: '',
   213→        kategorie: '',
   214→        datum: format(new Date(), 'yyyy-MM-dd'),
   215→        notizen: '',
   216→      });
   217→      setDialogOpen(false);
   218→
   219→      // Refresh data
   220→      await fetchData();
   221→    } catch (err) {
   222→      console.error('Fehler beim Speichern:', err);
   223→    } finally {
   224→      setSubmitting(false);
   225→    }
   226→  }
   227→
   228→  // Loading state
   229→  if (loading) {
   230→    return (
   231→      <div className="min-h-screen bg-background p-4 md:p-8" style={{ fontFamily: "'Plus Jakarta Sans', sans-serif" }}>
   232→        <div className="max-w-6xl mx-auto space-y-6">
   233→          <div className="flex items-center justify-between">
   234→            <Skeleton className="h-8 w-40" />
   235→            <Skeleton className="h-8 w-28" />
   236→          </div>
   237→          <Skeleton className="h-48 w-full" />
   238→          <div className="grid md:grid-cols-2 gap-6">
   239→            <Skeleton className="h-64" />
   240→            <Skeleton className="h-64" />
   241→          </div>
   242→        </div>
   243→      </div>
   244→    );
   245→  }
   246→
   247→  // Error state
   248→  if (error) {
   249→    return (
   250→      <div className="min-h-screen bg-background p-4 md:p-8 flex items-center justify-center" style={{ fontFamily: "'Plus Jakarta Sans', sans-serif" }}>
   251→        <Alert variant="destructive" className="max-w-md">
   252→          <AlertCircle className="h-4 w-4" />
   253→          <AlertTitle>Fehler</AlertTitle>
   254→          <AlertDescription className="mt-2">
   255→            {error.message}
   256→            <Button variant="outline" size="sm" className="mt-4 w-full" onClick={fetchData}>
   257→              Erneut versuchen
   258→            </Button>
   259→          </AlertDescription>
   260→        </Alert>
   261→      </div>
   262→    );
   263→  }
   264→
   265→  return (
   266→    <div className="min-h-screen bg-background" style={{ fontFamily: "'Plus Jakarta Sans', sans-serif" }}>
   267→      {/* Mobile Layout */}
   268→      <div className="md:hidden">
   269→        <div className="p-4 pb-24">
   270→          {/* Header */}
   271→          <header className="flex items-center justify-between mb-6">
   272→            <h1 className="text-lg font-semibold text-foreground">Ausgabentracker</h1>
   273→            <Badge variant="secondary" className="font-medium">
   274→              {format(now, 'MMMM yyyy', { locale: de })}
   275→            </Badge>
   276→          </header>
   277→
   278→          {/* Hero Section */}
   279→          <div className="py-8 text-center">
   280→            <p className="text-sm uppercase tracking-wider text-muted-foreground mb-2">
   281→              Ausgaben diesen Monat
   282→            </p>
   283→            <p className="text-[72px] font-bold leading-none text-foreground tabular-nums">
   284→              {formatCurrency(currentMonthTotal)}
   285→            </p>
   286→            {percentChange !== null && (
   287→              <div className={`flex items-center justify-center gap-1 mt-3 text-sm ${percentChange > 0 ? 'text-destructive' : 'text-[hsl(160_50%_40%)]'}`}>
   288→                {percentChange > 0 ? (
   289→                  <TrendingUp className="h-4 w-4" />
   290→                ) : (
   291→                  <TrendingDown className="h-4 w-4" />
   292→                )}
   293→                <span>
   294→                  {percentChange > 0 ? '+' : ''}{percentChange.toFixed(1)}% vs. letzter Monat
   295→                </span>
   296→              </div>
   297→            )}
   298→            <p className="text-sm text-muted-foreground mt-2">
   299→              {currentMonthExpenses.length} Transaktionen
   300→            </p>
   301→          </div>
   302→
   303→          {/* Category Breakdown */}
   304→          <Card className="mb-6 shadow-sm">
   305→            <CardHeader className="pb-3">
   306→              <CardTitle className="text-base font-semibold">Nach Kategorie</CardTitle>
   307→            </CardHeader>
   308→            <CardContent className="space-y-4">
   309→              {categoryBreakdown.length === 0 ? (
   310→                <p className="text-sm text-muted-foreground text-center py-4">
   311→                  Noch keine Ausgaben diesen Monat
   312→                </p>
   313→              ) : (
   314→                categoryBreakdown.map((cat, index) => (
   315→                  <div key={cat.name} className="space-y-1.5">
   316→                    <div className="flex items-center justify-between text-sm">
   317→                      <span className="font-medium">{cat.name}</span>
   318→                      <span className="text-muted-foreground">{formatCurrency(cat.total)}</span>
   319→                    </div>
   320→                    <div className="h-2 bg-muted rounded-full overflow-hidden">
   321→                      <div
   322→                        className="h-full rounded-full transition-all duration-500"
   323→                        style={{
   324→                          width: `${currentMonthTotal > 0 ? (cat.total / currentMonthTotal) * 100 : 0}%`,
   325→                          backgroundColor: CATEGORY_COLORS[index % CATEGORY_COLORS.length],
   326→                        }}
   327→                      />
   328→                    </div>
   329→                  </div>
   330→                ))
   331→              )}
   332→            </CardContent>
   333→          </Card>
   334→
   335→          {/* Recent Expenses */}
   336→          <Card className="shadow-sm">
   337→            <CardHeader className="pb-3">
   338→              <CardTitle className="text-base font-semibold">Letzte Ausgaben</CardTitle>
   339→            </CardHeader>
