     1â†’import { useState, useEffect, useMemo } from 'react';
     2â†’import type { Ausgaben, Kategorien } from '@/types/app';
     3â†’import { LivingAppsService, extractRecordId, createRecordUrl, APP_IDS } from '@/services/livingAppsService';
     4â†’import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
     5â†’import { Button } from '@/components/ui/button';
     6â†’import { Input } from '@/components/ui/input';
     7â†’import { Label } from '@/components/ui/label';
     8â†’import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
     9â†’import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
    10â†’import { Skeleton } from '@/components/ui/skeleton';
    11â†’import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
    12â†’import { Textarea } from '@/components/ui/textarea';
    13â†’import { Badge } from '@/components/ui/badge';
    14â†’import { AreaChart, Area, XAxis, YAxis, Tooltip, ResponsiveContainer, CartesianGrid } from 'recharts';
    15â†’import { AlertCircle, TrendingUp, TrendingDown, PlusCircle, Calendar, Euro, Car } from 'lucide-react';
    16â†’import { format, parseISO, startOfMonth, endOfMonth, eachDayOfInterval, startOfWeek, endOfWeek } from 'date-fns';
    17â†’import { de } from 'date-fns/locale';
    18â†’
    19â†’interface ExpenseDetail extends Ausgaben {
    20â†’  kategorieName?: string;
    21â†’}
    22â†’
    23â†’interface CategorySummary {
    24â†’  categoryId: string;
    25â†’  categoryName: string;
    26â†’  total: number;
    27â†’  percentage: number;
    28â†’  count: number;
    29â†’}
    30â†’
    31â†’export default function Dashboard() {
    32â†’  const [ausgaben, setAusgaben] = useState<Ausgaben[]>([]);
    33â†’  const [kategorien, setKategorien] = useState<Kategorien[]>([]);
    34â†’  const [loading, setLoading] = useState(true);
    35â†’  const [error, setError] = useState<Error | null>(null);
    36â†’  const [selectedMonth, setSelectedMonth] = useState(new Date());
    37â†’  const [dialogOpen, setDialogOpen] = useState(false);
    38â†’  const [detailDialogOpen, setDetailDialogOpen] = useState(false);
    39â†’  const [selectedExpense, setSelectedExpense] = useState<ExpenseDetail | null>(null);
    40â†’  const [submitting, setSubmitting] = useState(false);
    41â†’
    42â†’  // Form state
    43â†’  const [formData, setFormData] = useState({
    44â†’    datum: format(new Date(), 'yyyy-MM-dd'),
    45â†’    kategorie: '',
    46â†’    beschreibung: '',
    47â†’    betrag: '',
    48â†’    notizen: '',
    49â†’  });
    50â†’
    51â†’  // Fetch data
    52â†’  useEffect(() => {
    53â†’    async function fetchData() {
    54â†’      try {
    55â†’        setLoading(true);
    56â†’        const [ausgabenData, kategorienData] = await Promise.all([
    57â†’          LivingAppsService.getAusgaben(),
    58â†’          LivingAppsService.getKategorien(),
    59â†’        ]);
    60â†’        setAusgaben(ausgabenData);
    61â†’        setKategorien(kategorienData);
    62â†’      } catch (err) {
    63â†’        setError(err instanceof Error ? err : new Error('Fehler beim Laden der Daten'));
    64â†’      } finally {
    65â†’        setLoading(false);
    66â†’      }
    67â†’    }
    68â†’    fetchData();
    69â†’  }, []);
    70â†’
    71â†’  // Create category lookup map
    72â†’  const kategorienMap = useMemo(() => {
    73â†’    const map = new Map<string, Kategorien>();
    74â†’    kategorien.forEach(k => map.set(k.record_id, k));
    75â†’    return map;
    76â†’  }, [kategorien]);
    77â†’
    78â†’  // Filter expenses for current month
    79â†’  const currentMonthExpenses = useMemo(() => {
    80â†’    const start = startOfMonth(selectedMonth);
    81â†’    const end = endOfMonth(selectedMonth);
    82â†’    return ausgaben.filter(a => {
    83â†’      if (!a.fields.datum) return false;
    84â†’      const expenseDate = parseISO(a.fields.datum);
    85â†’      return expenseDate >= start && expenseDate <= end;
    86â†’    });
    87â†’  }, [ausgaben, selectedMonth]);
    88â†’
    89â†’  // Enrich expenses with category names
    90â†’  const enrichedExpenses = useMemo((): ExpenseDetail[] => {
    91â†’    return currentMonthExpenses.map(expense => {
    92â†’      const categoryId = extractRecordId(expense.fields.kategorie);
    93â†’      const category = categoryId ? kategorienMap.get(categoryId) : null;
    94â†’      return {
    95â†’        ...expense,
    96â†’        kategorieName: category?.fields.kategoriename || 'Ohne Kategorie',
    97â†’      };
    98â†’    });
    99â†’  }, [currentMonthExpenses, kategorienMap]);
   100â†’
   101â†’  // Calculate monthly total
   102â†’  const monthlyTotal = useMemo(() => {
   103â†’    return currentMonthExpenses.reduce((sum, a) => sum + (a.fields.betrag || 0), 0);
   104â†’  }, [currentMonthExpenses]);
   105â†’
   106â†’  // Calculate weekly total
   107â†’  const weeklyTotal = useMemo(() => {
   108â†’    const start = startOfWeek(new Date(), { locale: de });
   109â†’    const end = endOfWeek(new Date(), { locale: de });
   110â†’    return ausgaben
   111â†’      .filter(a => {
   112â†’        if (!a.fields.datum) return false;
   113â†’        const expenseDate = parseISO(a.fields.datum);
   114â†’        return expenseDate >= start && expenseDate <= end;
   115â†’      })
   116â†’      .reduce((sum, a) => sum + (a.fields.betrag || 0), 0);
   117â†’  }, [ausgaben]);
   118â†’
   119â†’  // Calculate daily average
   120â†’  const dailyAverage = useMemo(() => {
   121â†’    const today = new Date();
   122â†’    const start = startOfMonth(selectedMonth);
   123â†’    const daysElapsed = Math.max(1, Math.floor((today.getTime() - start.getTime()) / (1000 * 60 * 60 * 24)) + 1);
   124â†’    return monthlyTotal / daysElapsed;
   125â†’  }, [monthlyTotal, selectedMonth]);
   126â†’
   127â†’  // Calculate category breakdown
   128â†’  const categoryBreakdown = useMemo((): CategorySummary[] => {
   129â†’    const categoryMap = new Map<string, { total: number; count: number; name: string }>();
   130â†’
   131â†’    currentMonthExpenses.forEach(expense => {
   132â†’      const categoryId = extractRecordId(expense.fields.kategorie);
   133â†’      const categoryName = categoryId
   134â†’        ? (kategorienMap.get(categoryId)?.fields.kategoriename || 'Ohne Kategorie')
   135â†’        : 'Ohne Kategorie';
   136â†’      const key = categoryId || 'none';
   137â†’
   138â†’      if (!categoryMap.has(key)) {
   139â†’        categoryMap.set(key, { total: 0, count: 0, name: categoryName });
   140â†’      }
   141â†’      const current = categoryMap.get(key)!;
   142â†’      current.total += expense.fields.betrag || 0;
   143â†’      current.count += 1;
   144â†’    });
   145â†’
   146â†’    return Array.from(categoryMap.entries())
   147â†’      .map(([categoryId, data]) => ({
   148â†’        categoryId,
   149â†’        categoryName: data.name,
   150â†’        total: data.total,
   151â†’        count: data.count,
   152â†’        percentage: monthlyTotal > 0 ? (data.total / monthlyTotal) * 100 : 0,
   153â†’      }))
   154â†’      .sort((a, b) => b.total - a.total);
   155â†’  }, [currentMonthExpenses, kategorienMap, monthlyTotal]);
   156â†’
   157â†’  // Prepare chart data (daily spending)
   158â†’  const chartData = useMemo(() => {
   159â†’    const start = startOfMonth(selectedMonth);
   160â†’    const end = endOfMonth(selectedMonth);
   161â†’    const days = eachDayOfInterval({ start, end });
   162â†’
   163â†’    const dailyTotals = new Map<string, number>();
   164â†’    currentMonthExpenses.forEach(expense => {
   165â†’      if (!expense.fields.datum) return;
   166â†’      const dateKey = expense.fields.datum.split('T')[0];
   167â†’      const current = dailyTotals.get(dateKey) || 0;
   168â†’      dailyTotals.set(dateKey, current + (expense.fields.betrag || 0));
   169â†’    });
   170â†’
   171â†’    return days.map(day => {
   172â†’      const dateKey = format(day, 'yyyy-MM-dd');
   173â†’      return {
   174â†’        date: format(day, 'd. MMM', { locale: de }),
   175â†’        fullDate: dateKey,
   176â†’        betrag: dailyTotals.get(dateKey) || 0,
   177â†’      };
   178â†’    });
   179â†’  }, [currentMonthExpenses, selectedMonth]);
   180â†’
   181â†’  // Recent expenses (sorted by date descending)
   182â†’  const recentExpenses = useMemo(() => {
   183â†’    return [...enrichedExpenses]
   184â†’      .sort((a, b) => {
   185â†’        const dateA = a.fields.datum || '';
   186â†’        const dateB = b.fields.datum || '';
   187â†’        return dateB.localeCompare(dateA);
   188â†’      })
   189â†’      .slice(0, 8);
   190â†’  }, [enrichedExpenses]);
   191â†’
   192â†’  // Handle form submission
   193â†’  async function handleSubmit(e: React.FormEvent) {
   194â†’    e.preventDefault();
   195â†’    if (!formData.betrag || !formData.datum) return;
   196â†’
   197â†’    setSubmitting(true);
   198â†’    try {
   199â†’      const apiData = {
   200â†’        datum: formData.datum,
   201â†’        kategorie: formData.kategorie ? createRecordUrl(APP_IDS.KATEGORIEN, formData.kategorie) : undefined,
   202â†’        beschreibung: formData.beschreibung || undefined,
   203â†’        betrag: parseFloat(formData.betrag),
   204â†’        notizen: formData.notizen || undefined,
   205â†’      };
   206â†’
   207â†’      await LivingAppsService.createAusgabenEntry(apiData);
   208â†’
   209â†’      // Refresh data
   210â†’      const newAusgaben = await LivingAppsService.getAusgaben();
   211â†’      setAusgaben(newAusgaben);
   212â†’
   213â†’      // Reset form
   214â†’      setFormData({
   215â†’        datum: format(new Date(), 'yyyy-MM-dd'),
   216â†’        kategorie: '',
   217â†’        beschreibung: '',
   218â†’        betrag: '',
   219â†’        notizen: '',
   220â†’      });
   221â†’      setDialogOpen(false);
   222â†’    } catch (err) {
   223â†’      console.error('Failed to create expense:', err);
   224â†’      alert('Fehler beim Speichern der Ausgabe');
   225â†’    } finally {
   226â†’      setSubmitting(false);
   227â†’    }
   228â†’  }
   229â†’
   230â†’  // Loading state
   231â†’  if (loading) {
   232â†’    return (
   233â†’      <div className="min-h-screen p-4 md:p-8">
   234â†’        <div className="max-w-7xl mx-auto">
   235â†’          <Skeleton className="h-12 w-64 mb-8" />
   236â†’          <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
   237â†’            <Skeleton className="h-48" />
   238â†’            <Skeleton className="h-48" />
   239â†’            <Skeleton className="h-48" />
   240â†’          </div>
   241â†’        </div>
   242â†’      </div>
   243â†’    );
   244â†’  }
   245â†’
   246â†’  // Error state
   247â†’  if (error) {
   248â†’    return (
   249â†’      <div className="min-h-screen p-4 md:p-8">
   250â†’        <div className="max-w-7xl mx-auto">
   251â†’          <Alert variant="destructive">
   252â†’            <AlertCircle className="h-4 w-4" />
   253â†’            <AlertTitle>Fehler</AlertTitle>
   254â†’            <AlertDescription>
   255â†’              {error.message}
   256â†’              <Button
   257â†’                variant="outline"
   258â†’                className="mt-2"
   259â†’                onClick={() => window.location.reload()}
   260â†’              >
   261â†’                Erneut versuchen
   262â†’              </Button>
   263â†’            </AlertDescription>
   264â†’          </Alert>
   265â†’        </div>
   266â†’      </div>
   267â†’    );
   268â†’  }
   269â†’
   270â†’  const monthName = format(selectedMonth, 'MMMM yyyy', { locale: de });
   271â†’
   272â†’  return (
   273â†’    <div className="min-h-screen">
   274â†’      {/* Header */}
   275â†’      <header className="sticky top-0 z-10 bg-card/95 backdrop-blur border-b border-border">
   276â†’        <div className="max-w-7xl mx-auto px-4 md:px-8 h-14 md:h-16 flex items-center justify-between">
   277â†’          <h1 className="text-lg md:text-xl font-semibold">Ausgabentracker</h1>
   278â†’          <div className="flex items-center gap-2 md:gap-4">
   279â†’            <Select
   280â†’              value={format(selectedMonth, 'yyyy-MM')}
   281â†’              onValueChange={(val) => setSelectedMonth(parseISO(val + '-01'))}
   282â†’            >
   283â†’              <SelectTrigger className="w-32 md:w-40">
   284â†’                <SelectValue />
   285â†’              </SelectTrigger>
   286â†’              <SelectContent>
   287â†’                {Array.from({ length: 12 }, (_, i) => {
   288â†’                  const date = new Date();
   289â†’                  date.setMonth(date.getMonth() - i);
   290â†’                  const value = format(date, 'yyyy-MM');
   291â†’                  const label = format(date, 'MMMM yyyy', { locale: de });
   292â†’                  return (
   293â†’                    <SelectItem key={value} value={value}>
   294â†’                      {label}
   295â†’                    </SelectItem>
   296â†’                  );
   297â†’                })}
   298â†’              </SelectContent>
   299â†’            </Select>
   300â†’
   301â†’            <Dialog open={dialogOpen} onOpenChange={setDialogOpen}>
   302â†’              <DialogTrigger asChild>
   303â†’                <Button className="hidden md:flex items-center gap-2">
   304â†’                  <PlusCircle className="h-4 w-4" />
   305â†’                  Ausgabe hinzufÃ¼gen
   306â†’                </Button>
   307â†’              </DialogTrigger>
   308â†’              <DialogContent className="sm:max-w-md">
   309â†’                <DialogHeader>
   310â†’                  <DialogTitle>Neue Ausgabe</DialogTitle>
   311â†’                </DialogHeader>
   312â†’                <form onSubmit={handleSubmit} className="space-y-4">
   313â†’                  <div>
   314â†’                    <Label htmlFor="datum">Datum</Label>
   315â†’                    <Input
   316â†’                      id="datum"
   317â†’                      type="date"
   318â†’                      required
   319â†’                      value={formData.datum}
   320â†’                      onChange={(e) => setFormData({ ...formData, datum: e.target.value })}
   321â†’                    />
   322â†’                  </div>
   323â†’                  <div>
   324â†’                    <Label htmlFor="kategorie">Kategorie</Label>
   325â†’                    <Select
   326â†’                      value={formData.kategorie}
   327â†’                      onValueChange={(val) => setFormData({ ...formData, kategorie: val })}
   328â†’                    >
   329â†’                      <SelectTrigger>
   330â†’                        <SelectValue placeholder="Kategorie wÃ¤hlen..." />
   331â†’                      </SelectTrigger>
   332â†’                      <SelectContent>
   333â†’                        {kategorien.map(k => (
   334â†’                          <SelectItem key={k.record_id} value={k.record_id}>
   335â†’                            {k.fields.kategoriename || 'Ohne Name'}
   336â†’                          </SelectItem>
   337â†’                        ))}
   338â†’                      </SelectContent>
   339â†’                    </Select>
   340â†’                  </div>
   341â†’                  <div>
   342â†’                    <Label htmlFor="beschreibung">Beschreibung</Label>
   343â†’                    <Input
   344â†’                      id="beschreibung"
   345â†’                      value={formData.beschreibung}
   346â†’                      onChange={(e) => setFormData({ ...formData, beschreibung: e.target.value })}
   347â†’                      placeholder="z.B. Einkauf bei Rewe"
   348â†’                    />
   349â†’                  </div>
   350â†’                  <div>
   351â†’                    <Label htmlFor="betrag">Betrag (EUR)</Label>
   352â†’                    <Input
   353â†’                      id="betrag"
   354â†’                      type="number"
   355â†’                      step="0.01"
   356â†’                      required
   357â†’                      value={formData.betrag}
   358â†’                      onChange={(e) => setFormData({ ...formData, betrag: e.target.value })}
   359â†’                      placeholder="0.00"
   360â†’                    />
   361â†’                  </div>
   362â†’                  <div>
   363â†’                    <Label htmlFor="notizen">Notizen (optional)</Label>
   364â†’                    <Textarea
   365â†’                      id="notizen"
   366â†’                      value={formData.notizen}
   367â†’                      onChange={(e) => setFormData({ ...formData, notizen: e.target.value })}
   368â†’                      placeholder="ZusÃ¤tzliche Informationen..."
   369â†’                      rows={3}
   370â†’                    />
   371â†’                  </div>
   372â†’                  <div className="flex gap-2 pt-2">
   373â†’                    <Button type="submit" className="flex-1" disabled={submitting}>
   374â†’                      {submitting ? 'Wird gespeichert...' : 'HinzufÃ¼gen'}
   375â†’                    </Button>
   376â†’                    <Button
   377â†’                      type="button"
   378â†’                      variant="outline"
   379â†’                      onClick={() => setDialogOpen(false)}
   380â†’                      disabled={submitting}
   381â†’                    >
   382â†’                      Abbrechen
   383â†’                    </Button>
   384â†’                  </div>
   385â†’                </form>
   386â†’              </DialogContent>
   387â†’            </Dialog>
   388â†’          </div>
   389â†’        </div>
   390â†’      </header>
   391â†’
   392â†’      {/* Main Content */}
   393â†’      <main className="max-w-7xl mx-auto px-4 md:px-8 py-6 md:py-8">
   394â†’        <div className="lg:grid lg:grid-cols-3 lg:gap-8">
   395â†’          {/* Left Column (Main Content) - 2/3 width on desktop */}
   396â†’          <div className="lg:col-span-2 space-y-6 md:space-y-8">
   397â†’            {/* Hero KPI - Monthly Total */}
   398â†’            <Card className="shadow-lg" style={{
   399â†’              borderRadius: '10px 14px 11px 13px',
   400â†’              boxShadow: '0 2px 8px hsla(25, 40%, 30%, 0.08), 0 0px 2px hsla(25, 40%, 30%, 0.04), 0 4px 16px hsla(12, 40%, 50%, 0.06)'
   401â†’            }}>
   402â†’              <CardContent className="p-6 md:p-8">
   403â†’                <div className="text-sm text-muted-foreground mb-2">
   404â†’                  Ausgaben {format(selectedMonth, 'MMMM', { locale: de })}
   405â†’                </div>
   406â†’                <div className="text-5xl md:text-6xl font-bold mb-2">
   407â†’                  {new Intl.NumberFormat('de-DE', {
   408â†’                    style: 'currency',
   409â†’                    currency: 'EUR',
   410â†’                  }).format(monthlyTotal)}
   411â†’                </div>
   412â†’                <div className="flex items-center gap-2 text-sm">
   413â†’                  {monthlyTotal > 0 ? (
   414â†’                    <>
   415â†’                      <TrendingUp className="h-4 w-4 text-destructive" />
   416â†’                      <span className="text-muted-foreground">
   417â†’                        {currentMonthExpenses.length} Ausgaben
   418â†’                      </span>
   419â†’                    </>
   420â†’                  ) : (
   421â†’                    <span className="text-muted-foreground">Keine Ausgaben</span>
   422â†’                  )}
   423â†’                </div>
   424â†’              </CardContent>
   425â†’            </Card>
   426â†’
   427â†’            {/* Quick Stats Row */}
   428â†’            <div className="grid grid-cols-3 gap-3 md:gap-4">
   429â†’              <Card>
   430â†’                <CardContent className="p-4">
   431â†’                  <div className="text-xs text-muted-foreground mb-1">Diese Woche</div>
   432â†’                  <div className="text-lg md:text-xl font-bold">
   433â†’                    {new Intl.NumberFormat('de-DE', {
   434â†’                      style: 'currency',
   435â†’                      currency: 'EUR',
   436â†’                      maximumFractionDigits: 0,
   437â†’                    }).format(weeklyTotal)}
   438â†’                  </div>
   439â†’                </CardContent>
   440â†’              </Card>
   441â†’              <Card>
   442â†’                <CardContent className="p-4">
   443â†’                  <div className="text-xs text-muted-foreground mb-1">Ã˜ pro Tag</div>
   444â†’                  <div className="text-lg md:text-xl font-bold">
   445â†’                    {new Intl.NumberFormat('de-DE', {
   446â†’                      style: 'currency',
   447â†’                      currency: 'EUR',
   448â†’                      maximumFractionDigits: 0,
   449â†’                    }).format(dailyAverage)}
   450â†’                  </div>
   451â†’                </CardContent>
   452â†’              </Card>
   453â†’              <Card>
   454â†’                <CardContent className="p-4">
   455â†’                  <div className="text-xs text-muted-foreground mb-1">Anzahl</div>
   456â†’                  <div className="text-lg md:text-xl font-bold">
   457â†’                    {currentMonthExpenses.length}
   458â†’                  </div>
   459â†’                </CardContent>
   460â†’              </Card>
   461â†’            </div>
   462â†’
   463â†’            {/* Category Breakdown */}
   464â†’            <div>
   465â†’              <h2 className="text-xl md:text-2xl font-semibold mb-4">Nach Kategorie</h2>
   466â†’              {categoryBreakdown.length > 0 ? (
   467â†’                <div className="grid gap-3 md:gap-4 md:grid-cols-2">
   468â†’                  {categoryBreakdown.slice(0, 6).map((cat) => (
   469â†’                    <Card key={cat.categoryId} className="hover:shadow-md transition-shadow">
   470â†’                      <CardContent className="p-4">
   471â†’                        <div className="flex justify-between items-start mb-2">
   472â†’                          <div className="font-semibold">{cat.categoryName}</div>
   473â†’                          <div className="text-lg font-bold text-accent">
   474â†’                            {new Intl.NumberFormat('de-DE', {
   475â†’                              style: 'currency',
   476â†’                              currency: 'EUR',
   477â†’                            }).format(cat.total)}
   478â†’                          </div>
   479â†’                        </div>
   480â†’                        <div className="relative h-2 bg-muted rounded-full overflow-hidden">
   481â†’                          <div
   482â†’                            className="absolute inset-y-0 left-0 bg-accent rounded-full"
