     1→import { useState, useEffect, useMemo } from 'react';
     2→import type { Ausgaben, Kategorien } from '@/types/app';
     3→import { APP_IDS } from '@/types/app';
     4→import { LivingAppsService, extractRecordId, createRecordUrl } from '@/services/livingAppsService';
     5→import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
     6→import { Button } from '@/components/ui/button';
     7→import { Input } from '@/components/ui/input';
     8→import { Label } from '@/components/ui/label';
     9→import { Textarea } from '@/components/ui/textarea';
    10→import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
    11→import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
    12→import { Skeleton } from '@/components/ui/skeleton';
    13→import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
    14→import { ScrollArea, ScrollBar } from '@/components/ui/scroll-area';
    15→import { AreaChart, Area, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';
    16→import { format, parseISO, startOfMonth, endOfMonth, isWithinInterval, subDays, startOfDay, isSameDay } from 'date-fns';
    17→import { de } from 'date-fns/locale';
    18→import { Plus, AlertCircle, Receipt } from 'lucide-react';
    19→
    20→// Currency formatter for German locale
    21→function formatCurrency(value: number | null | undefined): string {
    22→  if (value == null) return '€ 0,00';
    23→  return new Intl.NumberFormat('de-DE', {
    24→    style: 'currency',
    25→    currency: 'EUR',
    26→  }).format(value);
    27→}
    28→
    29→// Date formatter for German locale
    30→function formatDate(dateString: string | null | undefined): string {
    31→  if (!dateString) return '-';
    32→  try {
    33→    return format(parseISO(dateString), 'dd.MM.yyyy', { locale: de });
    34→  } catch {
    35→    return '-';
    36→  }
    37→}
    38→
    39→// Loading skeleton component
    40→function LoadingSkeleton() {
    41→  return (
    42→    <div className="min-h-screen bg-background p-4 md:p-8 animate-pulse">
    43→      <div className="max-w-[1200px] mx-auto">
    44→        {/* Header skeleton */}
    45→        <div className="flex justify-between items-center mb-8">
    46→          <Skeleton className="h-7 w-40" />
    47→          <Skeleton className="h-10 w-36 hidden md:block" />
    48→        </div>
    49→
    50→        {/* Hero skeleton */}
    51→        <div className="text-center py-12 mb-8">
    52→          <Skeleton className="h-4 w-24 mx-auto mb-4" />
    53→          <Skeleton className="h-14 w-48 mx-auto mb-2" />
    54→          <Skeleton className="h-5 w-32 mx-auto" />
    55→        </div>
    56→
    57→        {/* Content skeleton */}
    58→        <div className="grid md:grid-cols-[65%_35%] gap-8">
    59→          <Skeleton className="h-[340px] rounded-lg" />
    60→          <div className="space-y-6">
    61→            <Skeleton className="h-[200px] rounded-lg" />
    62→            <Skeleton className="h-[280px] rounded-lg" />
    63→          </div>
    64→        </div>
    65→      </div>
    66→    </div>
    67→  );
    68→}
    69→
    70→// Empty state component
    71→function EmptyState({ onAddExpense }: { onAddExpense: () => void }) {
    72→  return (
    73→    <div className="min-h-screen bg-background p-4 md:p-8 font-['Plus_Jakarta_Sans']">
    74→      <div className="max-w-[600px] mx-auto text-center py-20">
    75→        <div className="w-20 h-20 rounded-full bg-accent mx-auto mb-6 flex items-center justify-center">
    76→          <Receipt className="w-10 h-10 text-primary" />
    77→        </div>
    78→        <h2 className="text-2xl font-semibold mb-3">Keine Ausgaben vorhanden</h2>
    79→        <p className="text-muted-foreground mb-8">
    80→          Beginne damit, deine erste Ausgabe zu erfassen und behalte den Überblick über deine Finanzen.
    81→        </p>
    82→        <Button size="lg" onClick={onAddExpense}>
    83→          <Plus className="w-5 h-5 mr-2" />
    84→          Erste Ausgabe hinzufügen
    85→        </Button>
    86→      </div>
    87→    </div>
    88→  );
    89→}
    90→
    91→// Error state component
    92→function ErrorState({ error, onRetry }: { error: Error; onRetry: () => void }) {
    93→  return (
    94→    <div className="min-h-screen bg-background p-4 md:p-8 font-['Plus_Jakarta_Sans']">
    95→      <div className="max-w-[500px] mx-auto py-20">
    96→        <Alert variant="destructive">
    97→          <AlertCircle className="h-4 w-4" />
    98→          <AlertTitle>Fehler beim Laden</AlertTitle>
    99→          <AlertDescription className="mt-2">
   100→            <p className="mb-4">{error.message}</p>
   101→            <Button variant="outline" onClick={onRetry}>
   102→              Erneut versuchen
   103→            </Button>
   104→          </AlertDescription>
   105→        </Alert>
   106→      </div>
   107→    </div>
   108→  );
   109→}
   110→
   111→// Add expense form dialog
   112→function AddExpenseDialog({
   113→  open,
   114→  onOpenChange,
   115→  kategorien,
   116→  onSuccess
   117→}: {
   118→  open: boolean;
   119→  onOpenChange: (open: boolean) => void;
   120→  kategorien: Kategorien[];
   121→  onSuccess: () => void;
   122→}) {
   123→  const [formData, setFormData] = useState({
   124→    betrag: '',
   125→    beschreibung: '',
   126→    kategorie: '',
   127→    datum: format(new Date(), 'yyyy-MM-dd'),
   128→    notizen: '',
   129→  });
   130→  const [submitting, setSubmitting] = useState(false);
   131→  const [error, setError] = useState<string | null>(null);
   132→
   133→  async function handleSubmit(e: React.FormEvent) {
   134→    e.preventDefault();
   135→    setError(null);
   136→
   137→    if (!formData.betrag || !formData.beschreibung) {
   138→      setError('Bitte fülle alle Pflichtfelder aus.');
   139→      return;
   140→    }
   141→
   142→    setSubmitting(true);
   143→
   144→    try {
   145→      const apiData: Ausgaben['fields'] = {
   146→        betrag: parseFloat(formData.betrag.replace(',', '.')),
   147→        beschreibung: formData.beschreibung,
   148→        datum: formData.datum, // Format: YYYY-MM-DD (date/date type)
   149→        notizen: formData.notizen || undefined,
   150→        kategorie: formData.kategorie
   151→          ? createRecordUrl(APP_IDS.KATEGORIEN, formData.kategorie)
   152→          : undefined,
   153→      };
   154→
   155→      await LivingAppsService.createAusgabenEntry(apiData);
   156→
   157→      // Reset form
   158→      setFormData({
   159→        betrag: '',
   160→        beschreibung: '',
   161→        kategorie: '',
   162→        datum: format(new Date(), 'yyyy-MM-dd'),
   163→        notizen: '',
   164→      });
   165→
   166→      onOpenChange(false);
   167→      onSuccess();
   168→    } catch (err) {
   169→      setError(err instanceof Error ? err.message : 'Fehler beim Speichern');
   170→    } finally {
   171→      setSubmitting(false);
   172→    }
   173→  }
   174→
   175→  return (
   176→    <Dialog open={open} onOpenChange={onOpenChange}>
   177→      <DialogContent className="sm:max-w-md">
   178→        <DialogHeader>
   179→          <DialogTitle>Ausgabe hinzufügen</DialogTitle>
   180→        </DialogHeader>
   181→        <form onSubmit={handleSubmit} className="space-y-4">
   182→          {error && (
   183→            <Alert variant="destructive">
   184→              <AlertCircle className="h-4 w-4" />
   185→              <AlertDescription>{error}</AlertDescription>
   186→            </Alert>
   187→          )}
   188→
   189→          <div className="space-y-2">
   190→            <Label htmlFor="betrag">Betrag (EUR) *</Label>
   191→            <Input
   192→              id="betrag"
   193→              type="text"
   194→              inputMode="decimal"
   195→              placeholder="0,00"
   196→              value={formData.betrag}
   197→              onChange={(e) => setFormData({ ...formData, betrag: e.target.value })}
   198→              required
   199→            />
   200→          </div>
   201→
   202→          <div className="space-y-2">
   203→            <Label htmlFor="beschreibung">Beschreibung *</Label>
   204→            <Input
   205→              id="beschreibung"
   206→              type="text"
   207→              placeholder="z.B. Einkauf Supermarkt"
   208→              value={formData.beschreibung}
   209→              onChange={(e) => setFormData({ ...formData, beschreibung: e.target.value })}
   210→              required
   211→            />
   212→          </div>
   213→
   214→          <div className="space-y-2">
   215→            <Label htmlFor="kategorie">Kategorie</Label>
   216→            <Select
   217→              value={formData.kategorie || 'none'}
   218→              onValueChange={(v) => setFormData({ ...formData, kategorie: v === 'none' ? '' : v })}
   219→            >
   220→              <SelectTrigger>
   221→                <SelectValue placeholder="Kategorie wählen..." />
   222→              </SelectTrigger>
   223→              <SelectContent>
   224→                <SelectItem value="none">Keine Kategorie</SelectItem>
   225→                {kategorien.map((kat) => (
   226→                  <SelectItem key={kat.record_id} value={kat.record_id}>
   227→                    {kat.fields.kategoriename || 'Unbenannt'}
   228→                  </SelectItem>
   229→                ))}
   230→              </SelectContent>
   231→            </Select>
   232→          </div>
   233→
   234→          <div className="space-y-2">
   235→            <Label htmlFor="datum">Datum</Label>
   236→            <Input
   237→              id="datum"
   238→              type="date"
   239→              value={formData.datum}
   240→              onChange={(e) => setFormData({ ...formData, datum: e.target.value })}
   241→            />
   242→          </div>
   243→
   244→          <div className="space-y-2">
   245→            <Label htmlFor="notizen">Notizen</Label>
   246→            <Textarea
   247→              id="notizen"
   248→              placeholder="Optionale Notizen..."
   249→              value={formData.notizen}
   250→              onChange={(e) => setFormData({ ...formData, notizen: e.target.value })}
   251→              rows={2}
   252→            />
   253→          </div>
   254→
   255→          <div className="flex justify-end gap-3 pt-4">
   256→            <Button type="button" variant="outline" onClick={() => onOpenChange(false)}>
   257→              Abbrechen
   258→            </Button>
   259→            <Button type="submit" disabled={submitting}>
   260→              {submitting ? 'Speichern...' : 'Speichern'}
   261→            </Button>
   262→          </div>
   263→        </form>
   264→      </DialogContent>
   265→    </Dialog>
   266→  );
   267→}
   268→
   269→// Category pill for mobile horizontal scroll
   270→function CategoryPill({ name, amount }: { name: string; amount: number }) {
   271→  return (
   272→    <div className="flex-shrink-0 px-4 py-2 rounded-full bg-accent text-accent-foreground text-sm font-medium whitespace-nowrap">
   273→      {name} {formatCurrency(amount)}
   274→    </div>
   275→  );
   276→}
   277→
   278→// Category bar for desktop
   279→function CategoryBar({
   280→  name,
   281→  amount,
