   340→// Main Dashboard component
   341→export default function Dashboard() {
   342→  const [ausgaben, setAusgaben] = useState<Ausgaben[]>([]);
   343→  const [kategorien, setKategorien] = useState<Kategorien[]>([]);
   344→  const [loading, setLoading] = useState(true);
   345→  const [error, setError] = useState<Error | null>(null);
   346→  const [dialogOpen, setDialogOpen] = useState(false);
   347→  const [expandedExpenseId, setExpandedExpenseId] = useState<string | null>(null);
   348→
   349→  // Fetch data
   350→  async function fetchData() {
   351→    try {
   352→      setLoading(true);
   353→      setError(null);
   354→      const [ausgabenData, kategorienData] = await Promise.all([
   355→        LivingAppsService.getAusgaben(),
   356→        LivingAppsService.getKategorien(),
   357→      ]);
   358→      setAusgaben(ausgabenData);
   359→      setKategorien(kategorienData);
   360→    } catch (err) {
   361→      setError(err instanceof Error ? err : new Error('Unbekannter Fehler'));
   362→    } finally {
   363→      setLoading(false);
   364→    }
   365→  }
   366→
   367→  useEffect(() => {
   368→    fetchData();
   369→  }, []);
   370→
   371→  // Create category lookup map
   372→  const categoryMap = useMemo(() => {
   373→    const map = new Map<string, Kategorien>();
   374→    kategorien.forEach(kat => map.set(kat.record_id, kat));
   375→    return map;
   376→  }, [kategorien]);
   377→
   378→  // Calculate current month stats
   379→  const currentMonthStats = useMemo(() => {
   380→    const now = new Date();
   381→    const monthStart = startOfMonth(now);
   382→    const monthEnd = endOfMonth(now);
   383→
   384→    const thisMonthExpenses = ausgaben.filter(a => {
   385→      if (!a.fields.datum) return false;
   386→      try {
   387→        const date = parseISO(a.fields.datum);
   388→        return isWithinInterval(date, { start: monthStart, end: monthEnd });
   389→      } catch {
   390→        return false;
   391→      }
   392→    });
   393→
   394→    const total = thisMonthExpenses.reduce((sum, a) => sum + (a.fields.betrag || 0), 0);
   395→    const count = thisMonthExpenses.length;
   396→
   397→    return { total, count, expenses: thisMonthExpenses };
   398→  }, [ausgaben]);
   399→
   400→  // Calculate today's spending
   401→  const todayTotal = useMemo(() => {
   402→    const today = startOfDay(new Date());
   403→    return ausgaben
   404→      .filter(a => {
   405→        if (!a.fields.datum) return false;
   406→        try {
   407→          return isSameDay(parseISO(a.fields.datum), today);
   408→        } catch {
   409→          return false;
   410→        }
   411→      })
   412→      .reduce((sum, a) => sum + (a.fields.betrag || 0), 0);
   413→  }, [ausgaben]);
   414→
   415→  // Calculate category breakdown
   416→  const categoryBreakdown = useMemo(() => {
   417→    const breakdown = new Map<string, { name: string; amount: number }>();
   418→
   419→    currentMonthStats.expenses.forEach(expense => {
   420→      const categoryId = extractRecordId(expense.fields.kategorie);
   421→      const category = categoryId ? categoryMap.get(categoryId) : null;
   422→      const categoryName = category?.fields.kategoriename || 'Ohne Kategorie';
   423→      const key = categoryId || 'uncategorized';
   424→
   425→      const current = breakdown.get(key) || { name: categoryName, amount: 0 };
   426→      current.amount += expense.fields.betrag || 0;
   427→      breakdown.set(key, current);
   428→    });
   429→
   430→    return Array.from(breakdown.values())
   431→      .sort((a, b) => b.amount - a.amount);
   432→  }, [currentMonthStats.expenses, categoryMap]);
   433→
   434→  // Prepare chart data (last 30 days for desktop, 7 for mobile)
   435→  const chartData = useMemo(() => {
   436→    const days = 30;
   437→    const data: Array<{ date: string; label: string; amount: number }> = [];
