  useEffect(() => {
    async function loadData() {
      try {
        setLoading(true)
        setError(null)
        const [kategorienData, ausgabenData] = await Promise.all([
          LivingAppsService.getKategorien(),
          LivingAppsService.getAusgaben(),
        ])
        setKategorien(kategorienData)
        setAusgaben(ausgabenData)
